INSERT INTO contents VALUES(101,replace(replace('A sqlite3 table was created by:\r\nCREATE TABLE IF NOT EXISTS contents (\r\nid INTEGER PRIMARY KEY AUTOINCREMENT,\r\nprompt TEXT,\r\nanswer TEXT,\r\nuserid INTEGER,\r\nFOREIGN KEY(userid) REFERENCES users(id)\r\n);\r\n\r\nwrite a golang function to search the prompt and answer column in the table.','\r',char(13)),'\n',char(10)),replace(replace('func searchContent(db *sql.DB, search string) ([]Content, error) {\r\n    var contents []Content\r\n    rows, err := db.Query("SELECT prompt, answer FROM contents WHERE prompt LIKE ''%''||?||''%'' OR answer LIKE ''%''||?||''%''", search, search)\r\n    if err != nil {\r\n        return nil, err\r\n    }\r\n    defer rows.Close()\r\n\r\n    for rows.Next() {\r\n        var content Content\r\n        if err := rows.Scan(&content.Prompt, &content.Answer); err != nil {\r\n            return nil, err\r\n        }\r\n        contents = append(contents, content)\r\n    }\r\n\r\n    if err := rows.Err(); err != nil {\r\n        return nil, err\r\n    }\r\n\r\n    return contents, nil\r\n}','\r',char(13)),'\n',char(10)),3,0);
