INSERT INTO contents VALUES(171,replace(replace('Extract a function delUserHandler from the following code, and modify the code the call the function:\r\n\r\n    r.GET("/userdel", withLogin(func(c *gin.Context) {\r\n        session := sessions.Default(c)\r\n        role := session.Get(userrole)\r\n        if role != 1 { \r\n            c.HTML(http.StatusNotFound, "user.tmpl", gin.H{"message": errors.New("Please login in as admin")})\r\n            return\r\n        }   \r\n        id, _ := strconv.Atoi(c.Query("id"))\r\n        err := delUser(db, id) \r\n        if err != nil {\r\n            c.HTML(http.StatusNotFound, "user.tmpl", gin.H{"message": err})\r\n        } else {\r\n            c.Redirect(http.StatusSeeOther, "/users")\r\n        }\r\n    }))','\r',char(13)),'\n',char(10)),replace('func delUserHandler(c *gin.Context) {\n        session := sessions.Default(c)\n        role := session.Get(userrole)\n        if role != 1 {\n            c.HTML(http.StatusNotFound, "user.tmpl", gin.H{"message": errors.New("Please login in as admin")})\n            return\n        }\n        id, _ := strconv.Atoi(c.Query("id"))\n        err := delUser(db, id)\n        if err != nil {\n            c.HTML(http.StatusNotFound, "user.tmpl", gin.H{"message": err})\n        } else {\n            c.Redirect(http.StatusSeeOther, "/users")\n        }\n    }\n\nr.GET("/userdel", withLogin(delUserHandler))','\n',char(10)),1,0);
INSERT INTO contents VALUES(172,replace(replace('Merge the following two routes to one route:\r\n\r\n    r.GET("/users", withLogin(func(c *gin.Context) {\r\n        users, _ := getUsers(db)   \r\n        session := sessions.Default(c)\r\n        role := session.Get(userrole)\r\n        c.HTML(http.StatusOK, "user.tmpl", gin.H{"users": users, "role": role})\r\n    }))\r\n    r.GET("/user", withLogin(func(c *gin.Context) {\r\n        id, _ := strconv.Atoi(c.Query("id"))\r\n        if id == 0 {\r\n            session := sessions.Default(c)\r\n            user := session.Get(userkey)\r\n            id = user.(int)\r\n        }\r\n        foundUser, _ := findUser(db, id)\r\n        c.HTML(http.StatusOK, "user.tmpl", gin.H{"user": foundUser})\r\n    }))','\r',char(13)),'\n',char(10)),replace('r.GET("/users", withLogin(func(c *gin.Context) {\n    users, _ := getUsers(db)   \n    session := sessions.Default(c)\n    role := session.Get(userrole)\n    id, _ := strconv.Atoi(c.Query("id"))\n    var data gin.H\n    if id == 0 {\n        data = gin.H{"users": users, "role": role}\n    } else {\n        foundUser, _ := findUser(db, id)\n        data = gin.H{"user": foundUser}\n    }\n    c.HTML(http.StatusOK, "user.tmpl", data)\n}))','\n',char(10)),1,0);
INSERT INTO contents VALUES(173,replace(replace('Write a response to the following email\r\n\r\n\r\nHi Michael, we can certainly arrange this paperwork.. I’m new to ‘Hotels at Home’ and would be great to learn more so we can maximise any opportunities together. Any chance we can schedule a quick call? Note I on AU time zone.\r\n\r\n','\r',char(13)),'\n',char(10)),replace(replace('Hi there,\r\n\r\nThank you for your prompt response! I''m glad to hear that you''re willing to work with us on arranging the necessary paperwork. As for your request to schedule a quick call, we''d be more than happy to set something up. It''s always great to collaborate with our partners and maximize opportunities together.\r\n\r\nWe understand that you''re on the AU time zone, so we''ll do our best to find a suitable time that works for both of us. In the meantime, if there are any specific questions you have about ''Hotels at Home'', feel free to let us know and we''ll be happy to provide you with more information.\r\n\r\nLooking forward to speaking with you soon!\r\n\r\nBest Regards,\r\nMichael','\r',char(13)),'\n',char(10)),1,0);
INSERT INTO contents VALUES(174,replace(replace('Modify the following code to accept the continue value submitted from this form:\r\n\r\n        <form action="/input" method="post">\r\n            <textarea name="input" id="input" cols="80" rows="10" placeholder="Enter your prompt here">{{.input}}</textarea>\r\n            <br />\r\n            <br />\r\n            <label for="continue">Continue previous conversation</label> \r\n            <input type="checkbox" name="continue" id="continue" value="yes">\r\n            <br />\r\n            <br />\r\n            <input type="submit" id="submit" value="Submit" style="display:block;">\r\n        </form>\r\n\r\n\r\nfunc addContentHandler(c *gin.Context) {\r\n    db := c.MustGet("db").(*sql.DB)\r\n    session := sessions.Default(c)\r\n    userID := session.Get(userkey).(int)\r\n    input := c.PostForm("input")\r\n    output := chat(input)\r\n    c.HTML(http.StatusOK, "input.tmpl", gin.H{\r\n        "input":  input,\r\n        "output": output,\r\n    })\r\n    err := addContent(db, input, output, userID)\r\n    if err != nil {\r\n        log.Println(err)\r\n        c.HTML(http.StatusInternalServerError, "input.tmpl", gin.H{"error": "Failed to add content"})  \r\n        return\r\n    }\r\n}   ','\r',char(13)),'\n',char(10)),replace('func addContentHandler(c *gin.Context) {\n    db := c.MustGet("db").(*sql.DB)\n    session := sessions.Default(c)\n    userID := session.Get(userkey).(int)\n    input := c.PostForm("input")\n    output := chat(input)\n    continueVal := c.PostForm("continue")\n    if continueVal == "yes" {\n        // do something with the continue value here\n    }\n    c.HTML(http.StatusOK, "input.tmpl", gin.H{\n        "input":  input,\n        "output": output,\n    })\n    err := addContent(db, input, output, userID)\n    if err != nil {\n        log.Println(err)\n        c.HTML(http.StatusInternalServerError, "input.tmpl", gin.H{"error": "Failed to add content"})  \n        return\n    }\n}','\n',char(10)),1,0);
