INSERT INTO contents VALUES(177,replace(replace('Modify the following code to get the last record in contents table which submited by the user and is_delete = 0:\r\n\r\nfunc getContent(db *sql.DB) ([]Content, error) {\r\n    rows, err := db.Query("SELECT id, prompt, answer, userid FROM contents")\r\n    if err != nil {\r\n        return nil, err\r\n    }\r\n    defer rows.Close()\r\n    var contents []Content\r\n    for rows.Next() {\r\n        var content Content\r\n        err := rows.Scan(&content.ID, &content.Prompt, &content.Answer, &content.UserID)\r\n        if err != nil {\r\n            return nil, err\r\n        }\r\n        contents = append(contents, content)\r\n    }\r\n    return contents, rows.Err()\r\n}   ','\r',char(13)),'\n',char(10)),replace('func getContent(db *sql.DB, userID int) (Content, error) {\n    row := db.QueryRow("SELECT id, prompt, answer, userid FROM contents WHERE userid=? AND is_delete=0 ORDER BY id DESC LIMIT 1", userID)\n    var content Content\n    err := row.Scan(&content.ID, &content.Prompt, &content.Answer, &content.UserID)\n    if err != nil {\n        return Content{}, err\n    }\n    return content, nil\n}','\n',char(10)),1,0);
